eotw-copy-code widget

\*/

// ä¸é€‚åˆ
/* function copybuttonListener() {
  document.addEventListener('mouseenter', () => {
    const codeBlocks = document.querySelectorAll('pre');
    codeBlocks.forEach(codeBlock => {
      codeBlock.addEventListener('mouseenter', () => {
        addCopyButton();
      });
    });
  });
} */

module.exports = function addCopyButton() {
  // æ‰¾åˆ°å½“å‰é¡µé¢çš„æ‰€æœ‰ä»£ç å—
  const codeBlocks = document.querySelectorAll('pre');

  Swal.fire({
    title: codeBlocks.length ? `Enable copy code` : `å½“å‰é¡µé¢æ²¡æœ‰å‘ç°ä»£ç å—`,
    icon: codeBlocks.length ? 'success' : 'info',
    toast: true,
    showCancelButton: false,
    showConfirmButton: false,
    timer: 1500,
    position: 'top-end',
  });

  // å¦‚æœæ²¡æœ‰ä»£ç å—, ç»“æŸ
  if (!codeBlocks.length) {
    return;
  }

  codeBlocks.forEach((codeBlock) => {
    // codeBlock.addEventListener('mouseenter', () => {
    //   console.log('updated');
    // });
    // æŸ¥æ‰¾ copybutton æŒ‰é’®
    const existingButton = codeBlock.querySelector('.copy-button');
    // å¦‚æœå·²ç»æ·»åŠ è¿‡copybutton, ç»“æŸ
    if (existingButton) return;

    // TODO: not work
    // å¦‚æœä»£ç ä¸ºç©º, ä¸æ·»åŠ  copybutton æŒ‰é’®
    const codeElement = codeBlock.querySelector('code');
    if (!codeElement) return;

    // support group with tailwindcss
    codeBlock.classList.add('group');

    // è·å– code è¯­è¨€ç±»å‹
    const fileType = codeElement.className.match(/language-(\w+)/)?.[1] || '';

    classNames =
      'opacity-0 scale-0 group-hover:opacity-100 group-hover:scale-100 copy-button delay-200 float-right hover:bg-gray-200 transition-all duration-800 ease-in-out p-2';

    // æ·»åŠ  copybutton
    const copyButton = $tw.utils.domMaker('button', {
      text: fileType ? `${fileType}` : 'copy',
      class: fileType ? classNames : classNames + ' -m-4',
    });

    const notify = () => {
      Swal.fire({
        title: `Copied to clipboard`,
        icon: 'success',
        toast: true,
        showCancelButton: false,
        showConfirmButton: false,
        timer: 1500,
        position: 'top-end',
      });
    };

    copyButton.addEventListener('click', () => {
      // TODO: add click sound
      // copy to clipboard

      // IOS å¹¶ä¸æ”¯æŒnavigator, ç›®å‰ä¸æ‰“æ–­å†™å…¼å®¹ä»£ç 
      navigator?.clipboard
        ?.writeText(codeElement.textContent)
        .then(() => {
          if (typeof Swal?.fire === 'function') {
            notify();
          } else {
            copyButton.textContent = 'âœ… Copied!';
            setTimeout(() => {
              copyButton.textContent = fileType ? `${fileType} ğŸ“‹` : 'ğŸ“‹';
            }, 1000);
          }
        })
        .catch((err) => {
          console.log(err);
        });
    });

    codeElement?.parentNode?.insertBefore(copyButton, codeElement);
  });
};
","type":"application/javascript","module-type":"library"},"$:/plugins/oeyoews/neotw-copy-code/copycode-startup.js":{"title":"$:/plugins/oeyoews/neotw-copy-code/copycode-startup.js","text":"/*\
title: $:/plugins/oeyoews/neotw-copy-code/copycode-startup.js
type: application/javascript
module-type: startup

copyButton module

\*/
(function () {
  /*jslint node: true, browser: true */
  /*global $tw: false */
  'use strict';

  exports.name = 'copyButton-startup-hook';
  exports.platforms = ['browser'];
  exports.after = ['startup'];
  exports.synchronous = true;
  exports.startup = () => {
    const addCopyButton = require('$:/plugins/oeyoews/neotw-copy-code/add-copybutton.js');
    $tw.rootWidget.addEventListener('om-copy-code', () => {
      addCopyButton();
    });
  };
})();
