    class: galleryTitleClassList,
    text: title,
    children: [iconify],
    attributes: {
      title: 'ç‚¹å‡»æŸ¥çœ‹',
    },
  });

  const contentNode = createElement('div', {
    children: [galleryTitle],
  });

