module.exports={pluginname:"侧边栏宽度调整插件",warning:"需要安装 tailwindcss"};","type":"application/javascript","module-type":"library"},"$:/plugins/oeyoews/notebook-theme-sidebar-resizer/style.css":{"title":"$:/plugins/oeyoews/notebook-theme-sidebar-resizer/style.css","text":".oresizer-left {
  left: 0;
}
.oresizer-right {
  right: 0;
}

.oresizer {
  width: 5px;
  border-radius: 5px;
  height: 100%;
  position: absolute;
  top: 0;
  background-color: #f3f4f6;
}

.oresizer:hover {
  cursor: e-resize;
  background-color: #e5e7eb;
  transition: all;
}

.dark .oresizer {
  background-color: #6b7280;
}
.dark .oresizer:hover {
  background-color: #4b5563;
}
