"},"$:/plugins/oeyoews/neotw-hitokoto/script":{"title":"$:/plugins/oeyoews/neotw-hitokoto/script","tags":"$:/tags/RawMarkupWikified/BottomBody","text":"<!-- `<script src="https://v1.hitokoto.cn/?encode=js&select=%23hitokoto" defer>``</script>` -->

`<script>`
function hitokoto_footer() {
  fetch("https://v1.hitokoto.cn")
    .then((response) => response.json())
    .then((data) => {
      const hitokoto = document.querySelector("#h-text");
      hitokoto.innerText = data.hitokoto;
      const hitokoto_from = document.querySelector("#h-author");
      hitokoto_from.href = "https://hitokoto.cn/?uuid=" + data.uuid;
      hitokoto_from.innerText = "@" + data.from;
    })
    .catch(console.error);
}

hitokoto_footer();
setInterval(hitokoto_footer, 30000);
`</script>`
"},"$:/plugins/oeyoews/neotw-hitokoto/style":{"title":"$:/plugins/oeyoews/neotw-hitokoto/style","tags":"$:/tags/Stylesheet","type":"text/css","text":"footer {
	flex: 1 1 auto;
	display:flex !important;
	justify-content:center;
	align-items: center;
	z-index:-1;
	min-height:25px;
	color: gray;
	user-select: none;
	padding: 2px;
	font-size: 50%;
}

footer .inline-icon svg{
  height:1em;
}

#hitokoto, #h-author {
  background: linear-gradient(45deg, #12c2e9, #c471ed, #f64f59);
  -webkit-background-clip: text;
  color: transparent;
  font-style: italic;
  font-weight: 700;
}

.print {
    width: 500px;
    white-space: nowrap;
    overflow: hidden;
    -webkit-animation: dy 7s steps(30, end) infinite;
    animation: dy 7s steps(30, end) infinite;
  }
  @-webkit-keyframes dy {
    from {
      width: 0;
    }
  }
  @keyframes dy {
    from {
      width: 0;
    }
  }
"}}}
