$:/plugins/kookma/gatha/styles/main.text:
/*--------------- correct button svg color in sidebar */
.ga-sidebar button svg {
	fill: <<colour sidebar-tiddler-link-foreground>>;
}
.ga-sidebar button:hover svg {
	fill: <<colour sidebar-tiddler-link-foreground-hover>>;
}
.ga-sidebar button.tc-btn-invisible {
	color: <<colour sidebar-tiddler-link-foreground>>;
}
.ga-sidebar button.tc-btn-invisible:hover {
	color: <<colour sidebar-tiddler-link-foreground-hover>>;
}

/*--------------- tc-tree for tree macro*/
.ga-sidebar .tc-tree button {
	color:<<colour sidebar-tiddler-link-foreground>>;
	font-weight:500;
}
.ga-sidebar .tc-tree li:hover svg{
	fill: <<colour sidebar-tiddler-link-foreground-hover>>;
}

.ga-sidebar .tc-tree li span{
	color:<<colour sidebar-tiddler-link-foreground>>; 
}

/*--- for the add bulk of tiddlers button */
.ga-extra-button{
	opacity:0.4;
}

.ga-extra-label:hover .ga-extra-button{
	opacity:1;
}

/*--------------- extra tiddlers tree */
.ga-sidebar .tc-sidebar-tab-open .tc-sidebar-tab-open-item button svg {
	fill: <<colour sidebar-tiddler-link-foreground>>;
}
.ga-sidebar .tc-sidebar-tab-open .tc-sidebar-tab-open-item:hover  button svg {
	fill: <<colour sidebar-tiddler-link-foreground-hover>>;
}

/*--- toggle in add extra tiddlers ui */

.tc-btn-invisible.ga-toggle-result  svg {
	fill: <<colour sidebar-tiddler-link-foreground>>;
	width:1.1em;
	height:1.1em;
}
.tc-btn-invisible.ga-toggle-result:hover svg {
	fill: <<colour sidebar-tiddler-link-foreground-hover>>;
}

/*ther ui elements */
.ga-edit-publish{
  width:95%;
  margin-top:5px;
}

.ga-sidebar .tc-drop-down button:hover {
	color: <<colour tiddler-link-background>>;
	background-color: <<colour tiddler-link-foreground>>;
}

$:/plugins/kookma/gatha/styles/plugin-ta.text:
/* Newly added  */
.ga-plugin-info{
	width:100%;
}
.ga-plugin-edit-element-value input{
	width:100%;
}
td.ga-plugin-info-element-name{
	width:14rem;
}
/* the plugin-type input */
.ga-plugin-edit-element-value input[type=text].ga-plugin-edit-element-type {width:70%;}
.ga-plugin-edit-element-value select {
	width: calc(30% - 8px);
	border-width:2px;
}

/*---from tfc---*/

tr.ga-plugin-info-element td {
	padding-top: 0.4ex;
	padding-bottom: 0.4ex;
	vertical-align: baseline;
}

td.ga-plugin-info-element-name {
	text-align: right;
	vertical-align: baseline;
	white-space: nowrap;
	background-color: <<colour table-header-background>>;
	font-weight: bold;
}

td.ga-plugin-info-element-value {
	text-align: left;
	vertical-align: top;
}

span.ga-plugin-info-unspecified {
	font-style: italic;
	color: <<colour very-muted-foreground>>;
}

span.ga-plugin-info-unspecified:before {
	font-style: normal;
	content: "(";
}

span.ga-plugin-info-unspecified:after {
	font-style: normal;
	content: ")";
	padding-left: 0.15em;
}
