$:/plugins/kookma/narenj/styles/pinned.text:
/* prevent using the muted sidebar color */
.tc-sidebar-header .kk-pinned-tab  {
	color: <<colour foreground>>;
}

$:/plugins/kookma/narenj/styles/side-edi.text:
.kk-side-editor-droppable-area {
	margin-right:5px;
	min-height:20px;
	text-align:center;	
	border:1px dotted <<colour tab-divider>>;
	color: <<colour muted-foreground>>
}
.edit-with-70{
	width:70%
}
.kk-side-editor .tc-edit-texteditor,
.kk-side-editor  .tc-edit-texteditor-body {
