         * GAME OVER
         */
        destroy: function()
        {
            var opts = this._o;

            this.hide();
            removeEvent(this.el, 'mousedown', this._onMouseDown, true);
            removeEvent(this.el, 'touchend', this._onMouseDown, true);
            removeEvent(this.el, 'change', this._onChange);
            if (opts.keyboardInput) {
                removeEvent(document, 'keydown', this._onKeyChange);
            }
            if (opts.field) {
                removeEvent(opts.field, 'change', this._onInputChange);
                if (opts.bound) {
                    removeEvent(opts.trigger, 'click', this._onInputClick);
                    removeEvent(opts.trigger, 'focus', this._onInputFocus);
                    removeEvent(opts.trigger, 'blur', this._onInputBlur);
                }
            }
            if (this.el.parentNode) {
                this.el.parentNode.removeChild(this.el);
            }
        }

    };

    return Pikaday;
}));

$:/plugins/nico/projectify/styles/cards.text:
\rules only filteredtranscludeinline transcludeinline macrodef macrocallinline

.py-cards {
  display: flex;
  flex-wrap: wrap;
  gap: 20px;
  margin-bottom: 20px;
}

.py-cards span {
  width: calc(33% - 12px);
}

@media (max-width: {{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}) {
  .py-cards span {
    width: calc(50% - 11px);
  }
}

.py-card-wrapper {
  /* Default background-color */
  background-color: rgba(255,255,0,.15);
  border-radius: 8px;
  transition: box-shadow ease-in .14s;
}

.py-cards .tc-dragover .py-card-wrapper .py-card {
  background: rgba(0,125,255,.3);
  box-shadow: 0 0 0 4px rgba(0,125,255,.3);
}

.py-card {
  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  text-decoration: none;
  color: <<colour foreground>>;
  border-radius: 8px;
  box-shadow: 0 0 5px 0 rgba(0, 0, 0, 0.3);
  padding: 20px;
  height: 150px;
  font-size: .9em;
}

.py-card-wrapper:hover {
  box-shadow: 0 0 0 4px rgba(0,125,255,.3);
}

.py-card-head {
  overflow: hidden;
  height: 4.6em;
  line-height: 1.2em;
}

.py-card .py-card-head h3 {
  font-weight: bold;
  font-size: 1em;
  margin: 0;
  margin-right: 10px;
}

.py-card .py-card-options {
  position: absolute;
  top: 4px;
  right: 8px;
}

.py-card .py-description {
  opacity: .7;
  font-size: .9em;
}

.py-card .py-project-category {
  font-size: .9em;
  font-weight: bold;
  text-align: center;
  padding: 1px;
  margin-bottom: -20px;
  margin-left: -20px;
  margin-right: -20px;
  background: rgba(0,0,0,.1);
  border-bottom-left-radius: 8px;
  border-bottom-right-radius: 8px;
}

$:/plugins/nico/projectify/styles/catego.text:
\rules only filteredtranscludeinline transcludeinline macrodef macrocallinline

.py-category-pill {
  position: relative;
  width: 1em;
  height: 1em;
  display: inline-block;
  vertical-align: middle;
  border-radius: 50%;
  background: <<colour tiddler-background>>;
}

.py-category-pill::after {
  content: " ";
  position: absolute;
  left: 0;
  top: 0;
  right: 0;
  bottom: 0;
  border-radius: 50%;
  border: 1px solid <<colour muted-foreground>>;
}

.py-category-wrapper {
  margin-top: -4px;
  margin-bottom: 10px;
}

$:/plugins/nico/projectify/styles/date-p.text:
\rules only filteredtranscludeinline transcludeinline macrodef macrocallinline

.py-date-picker .py-date-today {
  color: <<colour primary>>;
}

.py-date-picker .py-date-today svg {
  fill: <<colour primary>>;
}

.py-date-picker .py-date-clear {
  color: #e73939;
}

.py-date-picker .py-date-clear svg {
  fill: #e73939;
}

.py-date-picker .pika-single {
  margin: 0;
  border: 0;
  padding: 6px;
  color: <<colour foreground>>;
  background: transparent;
}

.py-date-picker .pika-lendar {
  margin: 0;
  border: 0;
  width: 100%;
}

.py-date-picker .pika-lendar .pika-table {
  margin: 0;
}

.py-date-picker .pika-lendar .pika-table th {
  background: transparent;
  font-weight: bold;
  color: <<colour muted-foreground>>;
}

.py-date-picker .pika-lendar button,
.py-date-picker .pika-button {
  border-radius: 0;
  background-color: transparent;
  text-align: center;
}

.py-date-picker .pika-button {
  padding: 6px 8px !important;
}

.py-date-picker .pika-prev,
.py-date-picker .pika-next {
  padding: 4px;
  width: 20px;
}

.py-date-picker .pika-lendar button:hover,
.py-date-picker .pika-lendar button:active,
.py-date-picker .pika-next {
  background-color: <<colour primary>>;
  color: <<colour background>>;
  border-radius: 3px;
}

.py-date-picker .pika-lendar .is-selected .pika-button,
.py-date-picker .pika-lendar .has-event .pika-button {
  background: <<colour muted-foreground>>;
  color: <<colour tiddler-background>>;
  box-shadow: none;
  border-radius: 3px;
}

.py-date-picker .pika-label {
  background-color: transparent;
  color: <<colour foreground>>;
}

.py-date-picker .pika-lendar table th,
.py-date-picker .pika-lendar table td {
  border: 0 none;
}

$:/plugins/nico/projectify/styles/form-c.text:
\rules only filteredtranscludeinline transcludeinline macrodef macrocallinline

.py-btn {
  border-radius: 1.5em;
  border: 1px solid #ccc;
  background: <<colour tiddler-background>>;
  padding: .3em 1em;
  cursor: pointer;
  transition: box-shadow ease-in .1s;
}

.py-btn:focus {
  outline: 0 none;
}

.py-btn:active {
