$:/xp/ui/EditorToolbar/mark-dropdown.text:
\define statusTid() $:/config/mark-button/status

<$tiddler tiddler=<<statusTid>>>
<$radio field="text" value="color" default="color"> Color </$radio>&ensp;
<$radio field="text" value="highlight"> Highlight </$radio>&ensp;
<$radio field="text" value="css"> CSS </$radio>
</$tiddler>

<$reveal state=<<statusTid>> type="match" text="color" default="color" animate="yes">

{{$:/xp/mark-button/mark-dropdown/color}}

</$reveal>

<$reveal state=<<statusTid>> type="match" text="highlight" animate="yes">

{{$:/xp/mark-button/mark-dropdown/highlight}}

</$reveal>

<$reveal state=<<statusTid>> type="match" text="css" animate="yes">

{{$:/xp/mark-button/mark-dropdown/css}}

</$reveal>

$:/xp/mark-button/color.css.text:
/* 快速标记 WikiText 中「标题」和「列表」的颜色
** Red Green Blue Brown Gray Orange Pink Purple Turquoise Violet Yellow
*/

.red,
.r { color: red; fill: red; }

.green,
.g { color: green; fill: green; }

.blue,
.b { color: blue; fill: blue; }

.brown,
.bn { color: brown; fill: brown; }

.gray,
.gy { color: gray; fill: gray; }

.orange,
.o { color: orange; fill: orange; }

.pink,
.pk { color: pink; fill: pink; }

.purple,
.p { color: purple; fill: purple; }

.turquoise,
.t { color: turquoise; fill: turquoise; }

.violet,
.v { color: violet; fill: violet; }

.yellow,
.y { color: yellow; fill: yellow; }

$:/xp/mark-button/styles.text:
.mark-red,
.mark-orange,
.mark-yellow,
.mark-greenyellow,
.mark-springgreen,
.mark-blue,
.mark-purple,
.mark-black {
	border-radius: 1px;
	backdrop-filter: blur(6px);
}

.mark-red {
	background-color: Red;
	background: rgba(255,0,0,0.25);
	box-shadow: 0 0 5px rgba(255,0,0,0.4);
<$list filter="[{$:/palette}get[color-scheme]] +[else[light]match[dark]]">
	background-color: #300000;
	color: rgba(255,0,0,0.8);
	box-shadow: none;
</$list>
}

.mark-orange {
	background-color: Orange;
	background: rgba(255,165,0,0.5);
	box-shadow: 0 0 5px rgba(255,165,0,0.5);
<$list filter="[{$:/palette}get[color-scheme]] +[else[light]match[dark]]">
	background-color: #301e00;
	color: rgba(255,165,0,0.9);
	box-shadow: none;
</$list>
}

.mark-yellow {
	background-color: Yellow;
	background: rgba(255,255,0,1);
	box-shadow: 0 0 5px rgba(255,255,0,0.5);
<$list filter="[{$:/palette}get[color-scheme]] +[else[light]match[dark]]">
	background-color: #2f3100;
	color: rgba(255,255,0,0.9);
	box-shadow: none;
</$list>
}

.mark-greenyellow {
	background-color: GreenYellow;
	background: rgba(173,255,47,0.5);
	box-shadow: 0 0 5px rgba(173,255,47,0.5);
<$list filter="[{$:/palette}get[color-scheme]] +[else[light]match[dark]]">
	background-color: #213009;
	color: rgba(173,255,47,0.9);
	box-shadow: none;
</$list>
}

.mark-springgreen {
	background-color: SpringGreen;
	background: rgba(0,255,127,0.45);
	box-shadow: 0 0 5px rgba(0,255,127,0.5);
<$list filter="[{$:/palette}get[color-scheme]] +[else[light]match[dark]]">
	background-color: #083019;
	color: rgba(0,255,127,0.9);
	box-shadow: none;
</$list>
}

.mark-blue {
	background-color: Blue;
	background: rgba(0,0,255,0.55);
	color: White;
<$list filter="[{$:/palette}get[color-scheme]] +[else[light]match[dark]]">
	background-color: rgba(0,0,255,0.6);
	color: #e5e5ff;
</$list>
}

.mark-purple {
	background-color: Purple;
	background: rgba(128,0,128,0.65);
	color: White;
<$list filter="[{$:/palette}get[color-scheme]] +[else[light]match[dark]]">
	background-color: rgba(128,0,128,0.75);
	color: #e5e5ff;
</$list>
}

.mark-black {
	background-color: Black;
	background: rgba(0,0,0,0.65);
	color: White;
<$list filter="[{$:/palette}get[color-scheme]] +[else[light]match[dark]]">
	background-color: #cfcfcf;
	color: Black;
</$list>
}
